import React, { Component } from 'react';
//import axios from 'axios';
import TableRow from './TableRow';
import productService from './product.service';
export default class Index extends Component {

  constructor(props) {
      super(props);
      this.state = {products: []};
    }
    componentDidMount(){
      productService.getAllProd()
    //  axios.get('http://localhost:3000/products/all')
        .then(response => {
          this.setState({ products: response.data });
          console.log(this.state.products);
        })
        .catch(function (error) {
          console.log(error);
        })
    }
    tabRow(){
      return this.state.products.map(function(object, i){
          return <TableRow obj={object} key={i} />;
      });
    }
    delete() {
      productService.deleteProd(this.props.obj._id)
       // axios.get('http://localhost:3000/products/delete/'+this.props.obj._id)
            .then(console.log('Deleted'))
            .catch(err => console.log(err));
            this.componentDidMount();
    }
    render() {
      return (
        <div>
          <h3 align="center">products List</h3>
          <table className="table table-striped" style={{ marginTop: 20 }}>
            <thead>
              <tr>
                <th>Product Name</th>
                <th>Product Description</th>
                <th>Product Price</th>
                <th colSpan="2">Action</th>
              </tr>
            </thead>
            <tbody>
              { this.tabRow() }
            </tbody>
          </table>
        </div>
      );
    }
  }
