import React, { useState, useEffect } from "react";
import Create from "./index-component";
import TableRow from "./TableRow";
import productService from "./product.service";
const Index=(props)=> {
 
const [products,setProducts]=useState([]);
 useEffect(() => {
  retrieveProducts();
 }, []);
 const retrieveProducts=()=> {
    console.log("1111111");
    productService
      .getAllProd()
      .then((response) => {
        setProducts(response.data );
        console.log(response.data);
      })
      .catch(error=> {
        console.log(error);
      });
  }
  const addProduct = (product) => {
   // product.id = product.length + 1;
   setProducts([...products, product]);
  return <Create addProduct={addProduct} />
    
  };
 const tabRow=() =>{
    return products.map(function (object, i) {
      return <TableRow obj={object} key={i} />;
    });
  }
  
 // render() {
    return (
      <div>
        <div>
              <h2>Add user</h2>
              <button id="addProd" onClick={addProduct}>Add</button>
              
            </div>
        <h3 align="center">products List</h3>
        <table className="table table-striped" style={{ marginTop: 20 }}>
          <thead>
            <tr>
              <th>Product Name</th>
              <th>Product Description</th>
              <th>Product Price</th>
              <th colSpan="2">Action</th>
            </tr>
          </thead>
         <tbody>{tabRow()}</tbody>
        </table>
      </div>
    );
 // }
}
export default Index;
