import React, { Component } from 'react';
import axios from 'axios';

export default class Create extends Component {
  constructor(props) {
      super(props);
      this.onChangeProductName = this.onChangeProductName.bind(this);
      this.onChangeProductDescription = this.onChangeProductDescription.bind(this);
      this.onChangeProductPrice = this.onChangeProductPrice.bind(this);
      this.onSubmit = this.onSubmit.bind(this);

      this.state = {
        ProductName: '',
        ProductDescription: '',
        ProductPrice:'',
        message:''
      }
  }
  
  onChangeProductName(e) {
    this.setState({
        ProductName: e.target.value
    });
  }
  onChangeProductDescription(e) {
    this.setState({
        ProductDescription: e.target.value
    })  
  }
  onChangeProductPrice(e) {
    this.setState({
        ProductPrice: e.target.value
    })
  }

  onSubmit(e) {
    e.preventDefault();
    console.log(`The values are ${this.state.ProductName}, ${this.state.ProductDescription}, and ${this.state.ProductPrice}`)
    const obj = {
        ProductName: this.state.ProductName,
        ProductDescription: this.state.ProductDescription,
        ProductPrice: this.state.ProductPrice
    };
    var msg=$("#msg");
    axios.post('http://localhost:3000/products/add', obj)
        .then(res => {
          console.log(res.data);
          console.log(res.data.Product);
          this.setState({
            message: res.data.Product
          })
          setTimeout(() => this.setState({message:''}), 3000);
        });
    this.setState({
      ProductName: '',
      ProductDescription: '',
      ProductPrice: ''
    })
    
  }
  componentDidMount(){
    this.setState({message:''})
  }
  render() {
      return (
          <div style={{ marginTop: 10 }}>
            <div id="msg" class="alert alert-success">{ this.state.message }</div>
              <h3>Add New Business</h3>
              <form onSubmit={this.onSubmit}>
                  <div className="form-group">
                      <label>Product Name:  </label>
                      <input 
                        type="text" 
                        className="form-control" 
                        value={this.state.ProductName}
                        onChange={this.onChangeProductName}
                        />
                  </div>
                  <div className="form-group">
                      <label>Product Description: </label>
                      <input type="text" 
                        className="form-control"
                        value={this.state.ProductDescription}
                        onChange={this.onChangeProductDescription}
                        />
                  </div>
                  <div className="form-group">
                      <label>Product Price: </label>
                      <input type="text" 
                        className="form-control"
                        value={this.state.ProductPrice}
                        onChange={this.onChangeProductPrice}
                        />
                  </div>
                  <div className="form-group">
                      <input type="submit" value="Register Business" className="btn btn-primary"/>
                  </div>
              </form>
          </div>
      )
  }
}
