import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import productService from './product.service';
import Index from './index-component';
class TableRow extends Component {

  constructor(props) {
    super(props);
    this.delete = this.delete.bind(this);
}

delete() {
  productService.deleteProd(this.props.obj._id)
   // axios.get('http://localhost:3000/products/delete/'+this.props.obj._id)
        .then(console.log('Deleted'))
        .catch(err => console.log(err))
        Index.reload();
        //this.props.history('/productlist');
}
  render() {
    return (
        <tr>
          <td>
            {this.props.obj.ProductName}
          </td>
          <td>
            {this.props.obj.ProductDescription}
          </td>
          <td>
            {this.props.obj.ProductPrice}
          </td>
          <td>
          <Link to={"/edit/"+this.props.obj._id} className="btn btn-primary">Edit</Link>
          </td>
          <td>
          <button onClick={this.delete} className="btn btn-danger">Delete</button>
          </td>
        </tr>
    );
  }
}

export default TableRow;
